// ข้อมูลจังหวัด
const provinces = [
    'กรุงเทพมหานคร', 'กระบี่', 'กาญจนบุรี', 'กาฬสินธุ์', 'กำแพงเพชร','กำแพงเพชร','กำแพงเพชร','กำแพงเพชร','กำแพงเพชร','กำแพงเพชร','กำแพงเพชร'
    // เพิ่มข้อมูลจังหวัดต่อท้ายตรงนี้...
];

const itemsPerPage = 1;
const pagesToShow = 5;

function paginate(array, page_size, page_number) {
    return array.slice((page_number - 1) * page_size, page_number * page_size);
}

function displayData(data) {
    const dataList = document.getElementById('data-list');
    dataList.innerHTML = '';

    data.forEach(item => {
        const listItem = document.createElement('div');
        listItem.textContent = item;
        dataList.appendChild(listItem);
    });
}

function displayPagination(totalPages, currentPage) {
    const pagination = document.getElementById('pagination');
    pagination.innerHTML = '';

    const startPage = Math.max(1, currentPage - Math.floor(pagesToShow / 2));
    const endPage = Math.min(totalPages, startPage + pagesToShow - 1);

    // ปุ่มย้อนกลับ
    if (currentPage > 1) {
        const prevBtn = document.createElement('span');
        prevBtn.textContent = 'Previous';
        prevBtn.classList.add('pagination-btn');
        prevBtn.addEventListener('click', () => {
            const newData = paginate(provinces, itemsPerPage, currentPage - 1);
            displayData(newData);
            displayPagination(totalPages, currentPage - 1);
        });
        pagination.appendChild(prevBtn);
    }

    // ปุ่มหน้า
    for (let i = startPage; i <= endPage; i++) {
        const btn = document.createElement('span');
        btn.textContent = i;
        btn.classList.add('pagination-btn');

        btn.addEventListener('click', () => {
            const newData = paginate(provinces, itemsPerPage, i);
            displayData(newData);
            displayPagination(totalPages, i);
        });

        if (i === currentPage) {
            btn.classList.add('active');
        }

        pagination.appendChild(btn);
    }

    // ปุ่มถัดไป
    if (currentPage < totalPages) {
        const nextBtn = document.createElement('span');
        nextBtn.textContent = 'Next';
        nextBtn.classList.add('pagination-btn');
        nextBtn.addEventListener('click', () => {
            const newData = paginate(provinces, itemsPerPage, currentPage + 1);
            displayData(newData);
            displayPagination(totalPages, currentPage + 1);
        });
        pagination.appendChild(nextBtn);
    }
}

const initialData = paginate(provinces, itemsPerPage, 1);
displayData(initialData);

const totalPages = Math.ceil(provinces.length / itemsPerPage);
displayPagination(totalPages, 1);
